mapper.select:
select user_mileage
from UserData
where user_id = #{userId}

mapper.update:
update UserData
set user_mileage = #{userMileage}
where user_id = #{userId}

mapper.select:
 	select user_id
 	from (select rownum as rnum, sub.*
 		  from (select *
		 	    from Posting
		 	    where posting_recommend > 0
		 	      and posting_time > sysdate - #{period}
		 	      and posting_type = #{postingType}
		 	    order by posting_recommend desc
		 	    ) sub
		  )
	where rnum = 1

select product_id, product_name, product_cost, product_imgfile
from MileageProduct join UserMileage using (product_id)
where user_id = #{userId} and product_remain > 0

select *
from MileageProduct join UserMileage using (product_id)
where user_id = #{userId} and product_id = #{productId}

mapper.update:
update UserMileage
set product_remain = #{productRemain}
where user_id = #{userId} and product_id = #{productId}
