mapper.update:
update UserData
set phone_id = #{phoneId}
where user_id = #{userId}

mapper.select:
	select posting_title, RPAD(posting_content, LENGTHB(posting_content)+3, '.')
 	from (select rownum as rnum, sub.*
 		  from (select posting_title, RPAD(SUBSTR(posting_content, 1, 20) as posting_content
		 	    from Posting
		 	    where posting_recommend > 0
		 	      and posting_time > sysdate - #{period}
		 	      and posting_type = #{postingType}
		 	    order by posting_recommend desc
		 	    ) sub
		  )
	where rnum = 1

	select posting_title, RPAD(posting_content, LENGTHB(posting_content)+3, '.')
 	from (select rownum as rnum, sub.*
 		  from (select posting_title, RPAD(SUBSTR(posting_content, 1, 20) as posting_content
		 	    from Posting
		 	    where posting_type = #{postingType}
		 	    order by posting_time desc
		 	    ) sub
		  )
	where rnum = 1
