mapper.select:
select *
from Term
where term_year = #{termYear} and term_classify = #{termClassify}

select *
from Term
where term_start <= #{termEnd} and term_end >= #{termStart}

mapper.update:
update LectureApply
set is_president = '0'
where user_id = 
	  (select user_id
	   from (select rownum as rnum, sub.*
	  		 from(select user_id from UserData order by user_id) sub
	  		 )
	   where rnum != #{presidentNumber}	 
	  )
