mapper.select:
select user_id, user_name, user_mileage
from UserData
where user_id = #{userId}

mapper.update:
update UserData set 

	user_pass = #{userPass},


	pass_question = #{passQuestion},
	pass_answer = #{passAnswer}

where user_id = #{userId}

mapper.select:
select pass_question
from UserData
where user_id = #{userId}


select user_pass
from UserData
where user_id = #{userId}

select count(*)
from UserData
where user_id = #{userId}
